// /src/hooks/useAuth.tsx
import React, { useState, createContext, useContext, useEffect } from "react";

// Create the context 
const AuthContext = createContext(null);

export const AuthProvider = ({ children }) => {
   // Store boolean to indicate whether a user is authenticated (default False)
   const [authed, setAuthed] = useState(false);
   // Store new value to indicate the call has not finished. Default to true
   const [loading, setLoading] = useState(true);

   const login = async (googleData) => {
      // log in to the server
      const loginResult = await asyncServerLogin(googleData.tokenId);
      // if successful, set authed var to true
      if (loginResult) {
         console.log('Cruiser logged in successfully.');
         setAuthed(true);
      }
   }
   const logout = () => { return null; }

   const asyncServerLogin = async (googleTokenId) => {
      // fetch user data for our backend 
      const resp = await fetch('/api/login', {
         method: 'POST',
         body: JSON.stringify({
             token: googleTokenId
         }),
         headers: {
             'Content-Type': 'application/json'
         }
     })
     // return boolean from server
     const data = await resp.json();
     console.log(data)
     if (data) {
         return data.logged_in;
     } else {
        return false;
     }
   }

   // Runs once when the component first mounts
   useEffect(() => {
      authCheck().then((activeUser) => {
         if (activeUser) {
            console.log("fake async login check called");
            setAuthed(true);
            setLoading(false);
         } else {
            setAuthed(false);
            setLoading(false);
         }
      });
   }, []);

   // Mock call to an authentication endpoint 
   const authCheck = async () => {
      return new Promise((resolve, reject) => {
         setTimeout(() => {
            resolve("Logged In");
         }, 300);
      });
   }

   return (
      // Expose the new `loading` value so we can consume it in `App.tsx`
      <AuthContext.Provider
         value={{ authed, setAuthed, login, logout, loading }}
      >
         {children}
      </AuthContext.Provider>
   );
}

// Finally creating the custom hook 
export const useAuth = () => useContext(AuthContext);
